import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const GameStateContainer = styled.div`
  background-color: ${props => props.theme.colors.background};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius};
  padding: ${props => props.theme.space.large};
  margin-bottom: ${props => props.theme.space.large};
  box-shadow: ${props => props.theme.boxShadow};
`;

const Title = styled.h2`
  color: ${props => props.theme.colors.primary};
  margin-bottom: ${props => props.theme.space.medium};
`;

const Score = styled.p`
  font-size: ${props => props.theme.fontSizes.large};
  font-weight: bold;
  color: ${props => props.theme.colors.secondary};
`;

const StateItem = styled.p`
  margin-bottom: ${props => props.theme.space.small};
`;

function GameState() {
    const [gameState, setGameState] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // This is where you'd fetch the game state from your backend
        // For now, we'll simulate it with a timeout
        setTimeout(() => {
            setGameState({
                communityScore: 100,
                currentRound: 3,
                activePlayers: 5,
                timeRemaining: '2:30'
            });
            setLoading(false);
        }, 1000);
    }, []);

    if (loading) return <GameStateContainer><p>Loading game state...</p></GameStateContainer>;
    if (error) return <GameStateContainer><p>Error: {error}</p></GameStateContainer>;

    return (
        <GameStateContainer>
            <Title>Game State</Title>
            <Score>Community Score: {gameState.communityScore}</Score>
            <StateItem>Current Round: {gameState.currentRound}</StateItem>
            <StateItem>Active Players: {gameState.activePlayers}</StateItem>
            <StateItem>Time Remaining: {gameState.timeRemaining}</StateItem>
        </GameStateContainer>
    );
}

export default GameState;
