import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const FormContainer = styled.div`
  background-color: ${props => props.theme.colors.background};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius};
  padding: ${props => props.theme.space.large};
  margin-bottom: ${props => props.theme.space.large};
  box-shadow: ${props => props.theme.boxShadow};
`;

const Title = styled.h2`
  color: ${props => props.theme.colors.secondary};
  margin-bottom: ${props => props.theme.space.medium};
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  margin-bottom: ${props => props.theme.space.small};
  color: ${props => props.theme.colors.text};
`;

const Input = styled.input`
  margin-bottom: ${props => props.theme.space.medium};
  padding: ${props => props.theme.space.small};
  border: 1px solid ${props => props.theme.colors.primary};
  border-radius: ${props => props.theme.borderRadius};
`;

const Select = styled.select`
  margin-bottom: ${props => props.theme.space.medium};
  padding: ${props => props.theme.space.small};
  border: 1px solid ${props => props.theme.colors.primary};
  border-radius: ${props => props.theme.borderRadius};
`;

const SubmitButton = styled.button`
  background-color: ${props => props.theme.colors.secondary};
  color: ${props => props.theme.colors.white};
  padding: ${props => props.theme.space.small} ${props => props.theme.space.medium};
  border: none;
  border-radius: ${props => props.theme.borderRadius};
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

function ProposeActionForm() {
  const [actionType, setActionType] = useState('');
  const [betAmount, setBetAmount] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/propose_action', {
        actionType,
        betAmount: parseInt(betAmount)
      });
      setActionType('');
      setBetAmount('');
      // Optionally, you can add a callback to refresh the pending actions list in the parent component
    } catch (error) {
      console.error('Error proposing action:', error);
    }
  };

  return (
    <FormContainer>
      <Title>Propose Action</Title>
      <Form onSubmit={handleSubmit}>
        <Label htmlFor="actionType">Action Type:</Label>
        <Select
          id="actionType"
          value={actionType}
          onChange={(e) => setActionType(e.target.value)}
        >
          <option value="bet">Bet</option>
          <option value="fold">Fold</option>
          {/* Add more action types as needed */}
        </Select>
        <Label htmlFor="betAmount">Bet Amount:</Label>
        <Input
          type="number"
          id="betAmount"
          value={betAmount}
          onChange={(e) => setBetAmount(e.target.value)}
          min="0"
        />
        <SubmitButton type="submit">Propose Action</SubmitButton>
      </Form>
    </FormContainer>
  );
}

export default ProposeActionForm;
