const theme = {
    colors: {
        primary: '#1d3c3f',     // Dark teal
        secondary: '#dca956',   // Gold
        background: '#1b5360',  // Light gold 1b5360
        appBackground: '#1d3c3f', // Dark green
        componentBackground: '#7b3d16', // Brown
        text: '#dca956',        // Dark brown
        white: '#FFFFFF',
        error: '#FF4136',
        border: '#e7cc7f',      // Teal
    },
    fonts: {
        body: 'Inter, sans-serif',
        heading: 'Inter, sans-serif',
    },
    fontSizes: {
        small: '0.875rem',
        medium: '1rem',
        large: '1.25rem',
        xlarge: '1.5rem',
        xxlarge: '2rem',
    },
    space: {
        small: '0.5rem',
        medium: '1rem',
        large: '1.5rem',
        xlarge: '2rem',
        xxlarge: '3rem',
    },
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
};

export default theme;
