import axios from 'axios';

const API_URL = 'http://localhost:5000';  // Use the port your Flask app is running on

const api = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

export const getGameState = () => api.get('/game_state');
export const getPendingActions = () => api.get('/pending_actions');
export const proposeAction = (action) => api.post('/propose_action', action);
export const supportAction = (actionId, support) => api.post(`/support_action/${actionId}`, { support });
export const runGame = (outcome) => api.post('/run_game', { outcome });
export const resetGame = () => api.post('/reset_game');

export default api;
