import React from 'react';
import styled from 'styled-components';

const PendingActionsContainer = styled.div`
  background-color: ${props => props.theme.colors.background};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius};
  padding: ${props => props.theme.space.large};
  margin-bottom: ${props => props.theme.space.large};
  box-shadow: ${props => props.theme.boxShadow};
`;

const Title = styled.h2`
  color: ${props => props.theme.colors.primary};
  margin-bottom: ${props => props.theme.space.medium};
`;

const Action = styled.div`
  background-color: ${props => props.theme.colors.white};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius};
  padding: ${props => props.theme.space.medium};
  margin-bottom: ${props => props.theme.space.small};

  &:hover {
    box-shadow: ${props => props.theme.boxShadow};
  }
`;

const SupportButton = styled.button`
  background-color: ${props => props.theme.colors.secondary};
  color: ${props => props.theme.colors.white};
  padding: ${props => props.theme.space.small} ${props => props.theme.space.medium};
  border: none;
  border-radius: ${props => props.theme.borderRadius};
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

function PendingActions() {
    const pendingActions = [
        { id: 1, type: 'bet', proposer: 'Player 1', bet_amount: 100 },
        { id: 2, type: 'challenge', proposer: 'Player 2', bet_amount: 200 },
    ];

    const handleSupport = (actionId, betAmount) => {
        console.log(`Supporting action ${actionId} with bet amount ${betAmount}`);
    };

    return (
        <PendingActionsContainer>
            <Title>Pending Actions</Title>
            {pendingActions.map(action => (
                <Action key={action.id}>
                    <p>Type: {action.type}</p>
                    <p>Proposer: {action.proposer}</p>
                    <p>Bet Amount: {action.bet_amount}</p>
                    <SupportButton onClick={() => handleSupport(action.id, action.bet_amount)}>Support</SupportButton>
                </Action>
            ))}
        </PendingActionsContainer>
    );
}

export default PendingActions;
