import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { getGameState } from '../services/api';

const GameStateContainer = styled.div`
  background-color: ${props => props.theme.colors.componentBackground};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius};
  padding: ${props => props.theme.space.large};
  margin-bottom: ${props => props.theme.space.large};
  box-shadow: ${props => props.theme.boxShadow};
`;

function GameState() {
    const [gameState, setGameState] = useState(null);

    useEffect(() => {
        getGameState()
            .then(response => {
                setGameState(response.data);
            })
            .catch(error => {
                console.error("Error fetching game state:", error);
            });
    }, []);

    if (!gameState) return <p>Loading...</p>;

    return (
        <GameStateContainer>
            <h2>Game State</h2>
            <p>Community Score: {gameState.communityScore}</p>
            <p>Current Round: {gameState.currentRound}</p>
            <p>Active Players: {gameState.activePlayers}</p>
            <p>Time Remaining: {gameState.timeRemaining}</p>
        </GameStateContainer>
    );
}

export default GameState;
