const theme = {
    colors: {
        primary: '#0A2540',
        secondary: '#00D4FF',
        background: '#F6F9FC',
        appBackground: '#4682B4',  // Steel Blue (calmer ocean blue)
        componentBackground: '#FFFFFF',  // White background for components
        text: '#425466',
        white: '#FFFFFF',
        error: '#FF4136',
        border: '#E0E0E0',
    },
    fonts: {
        body: 'Inter, sans-serif',
        heading: 'Inter, sans-serif',
    },
    fontSizes: {
        small: '0.875rem',
        medium: '1rem',
        large: '1.25rem',
        xlarge: '1.5rem',
        xxlarge: '2rem',
    },
    space: {
        small: '0.5rem',
        medium: '1rem',
        large: '1.5rem',
        xlarge: '2rem',
        xxlarge: '3rem',
    },
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
};

export default theme;
